// Create a new list item when clicking on the "Add" button

function newElement(valueItem, flag) {

    var li = document.createElement("li");
    var t = document.createTextNode(valueItem);

    if (flag == "checked") {
        li.className += " checked";
    }

    li.appendChild(t);
    listElement.appendChild(li);
    listDataInput.value = "";



    var span = document.createElement("SPAN");
    var txt = document.createTextNode("\u00D7");
    span.className = "close";
    span.appendChild(txt);
    li.appendChild(span);

    for (var i = 0; i < close.length; i++) {
        close[i].onclick = function() {
            var div = this.parentElement;
            div.parentNode.removeChild(div);
        };
    }
}




function loadFromObject() {
    for (var i = 0; i < saveListsWireframe.saves.length; i++) {
        createListName(saveListsWireframe.saves[i].name, true);
    }

    for (var i = 0; i < saveListsWireframe.saves[0].data.flag.length; i++) {
        newElement(saveListsWireframe.saves[0].data.listData[i], saveListsWireframe.saves[0].data.flag[i]);
    }
    listDataInput.disabled = false;
}


function createListName(saveListName, bool) {
    var childOptionElement = document.createElement("option");
    childOptionElement.text = saveListName;
    childOptionElement.value = saveListName;
    selectElement.add(childOptionElement);
    if (!bool) {
        selectElement.selectedIndex = selectElement.length - 1;
        selectElement.options[selectElement.selectedIndex].selected = true;
    } else {
        selectElement.selectedIndex = 0;
        selectElement.options[selectElement.selectedIndex].selected = true;
    }


}


function addField() {
    var field = { "name": "", "data": { "flag": [], "listData": [] } };
    var saveListName = document.getElementById("selectName").value;

    if (saveListName === null) {
        return;
    }

    if ((selectElement.options[selectElement.selectedIndex]) !== undefined) {
        for (var i = 0; i < saveListsWireframe.saves.length; i++) {

            if (saveListName == saveListsWireframe.saves[i].name) {
                console.log("Name taken try another");
                selectListNameInput.value = "";
                return;
            }
        }

        saveListObject();


        if (saveListName !== "") {
            deleteList(true);
            field.name = saveListName;
            saveListsWireframe.saves.push(field);
            createListName(saveListName);

            if (selectElement.length >= 1) {
                listDataInput.disabled = false;
            }
        }
        listDataInput.value = "";
        return;
    }
    if (saveListName !== "") {
        field.name = saveListName;
        saveListsWireframe.saves[0] = field;
        createListName(saveListName);
        if (selectElement.length >= 1) {
            listDataInput.disabled = false;
        }

    }  
}


function deleteOptionList(){
    while (selectElement.firstChild) {
        selectElement.removeChild(selectElement.firstChild);
    }
}



function deleteList(bool) {
    while (listElement.firstChild) {
        listElement.removeChild(listElement.firstChild);
    }
    if (!bool) {
        saveListsWireframe.saves.splice([selectElement.selectedIndex],1);
        selectElement.remove(selectElement.selectedIndex);

    }

}



function changeList() {
    if (selectElement.options[selectElement.selectedIndex] === undefined) {
        return;
    }
    var key = selectElement.options[selectElement.selectedIndex].value;
    deleteList(true);

    for (var i = 0; i < saveListsWireframe.saves.length; i++) {
        if (saveListsWireframe.saves[i].name == key) {
            for (var j = 0; j < saveListsWireframe.saves[i].data.flag.length; j++) {

                newElement(saveListsWireframe.saves[i].data.listData[j], saveListsWireframe.saves[i].data.flag[j]);
            }
        }
    }
}


function saveListObject() {
    var items = listElement.getElementsByTagName("li");
    var key = selectElement.options[selectElement.selectedIndex].value;
    var itemsArray = [];
    var keyArrays = [];


    for (i = 0; i < items.length; i++) {
        itemsArray.push(items[i].childNodes[0].nodeValue);
        keyArrays.push(hasClassString(items[i], "checked"));

    }


    for (var i = 0; i < saveListsWireframe.saves.length; i++) {
        if (saveListsWireframe.saves[i].name == key) {

            saveListsWireframe.saves[i]["data"]["listData"] = itemsArray;
            saveListsWireframe.saves[i]["data"]["flag"] = keyArrays;

        }
    }
}


