//////  helper functions


function hasClassString(element, cls) {
    if (hasClass(element, cls) === true) {
        return "checked";
    } else {
        return "unchecked";
    }
}


function hasClass(element, cls) {
    return (' ' + element.className + ' ').indexOf(' ' + cls + ' ') > -1;
}


////////    save and load  and clear storage  /////////////


function clearStorage() {
    /// clear local storage on browser

    localStorage.clear();

}

function saveToStorage(){
        saveListObject();
    //// save to Chrome storage
        saveToChrome();

}

function loadFromStorage(){

    //// load to Chrome storage

         loadFromChrome();

}



function saveToChrome() {
    localStorage.setItem('todoListSave', JSON.stringify(saveListsWireframe));

}



function loadFromChrome() {
    if (localStorage.getItem('todoListSave') !== null) {
        saveListsWireframe = JSON.parse(localStorage.getItem('todoListSave'));
        loadFromObject();
    }

}

