$(document).ready(function() {
    var saveObject = {};
    var textCopy = "";
    populateClipboard();
    var textField = document.getElementById("textfied");
  // Click to remove information
  $(".information").on("click", function(e){
    e.preventDefault();
    $(this).css("display", "none");
  });
    /*
     * PROGRAM INPUT FIELD
     */
    $("input#addToDo").on("keypress", function(event) {

        if (event.which === 13) {

            if (!(jQuery.isEmptyObject(saveObject))) {
                for (var key in saveObject) {
                    if (key == $(this).val()) {
                        return;
                    }
                }
            }

            var newToDo = $(this).val();
            $("ul").prepend("<li><span><i class='fa fa-trash delete'></i></span><span class='copySpan'><i class='fa fa-clipboard copy'></i></span>" + newToDo + "</li>");
            newToDo = $(this).val('');
        }
    });

    /*
     * DELETE ITEMS ON LIST
     */
    $("ul").on("click", ".copy", function(event) {
        event.stopPropagation();
        if (textField.value !== "") {
            saveObject[$(this).parent().parent().text()] = textField.value;
            textField.value = "";
            saveToStorageChrome();
        }

    });

    $("ul").on("click", ".delete", function(event) {

        $(this).parent().fadeOut(500, function() {
            $(this).parent().remove();
        });
        var key = $(this).parent().parent().last().text();
        delete saveObject[key];
        //Ensures that no other parent events trigger.
        event.stopPropagation();
        saveToStorageChrome();
    });
    /*
     * Copy to clipboard
     */
    $("ul").on("click", "li", function() {
        textCopy = saveObject[$(this).text()];
    });

    var clipboard = new Clipboard('li', {
        text: function() {
            return textCopy;
        }
    });

    function populateClipboard() {
        loadFromStorage();
        if (!(jQuery.isEmptyObject(saveObject))) {
            for (var key in saveObject) {
                $("ul").prepend("<li><span><i class='fa fa-trash delete'></i></span><span class='copySpan'><i class='fa fa-clipboard copy'></i></span>" + key + "</li>");

            }
        }
    }


    function saveToStorageChrome() {
        for (var key in saveObject) {
            if (saveObject[key] === "") {
                delete saveObject[key];
            }
        }
        saveToStorage();
    }


// Chrome App version uses chrome storage large sync library from 
// --> https://github.com/dtuit/chrome-storage-largeSync

    function saveToStorage() {
        saveToChrome();
    }

    function loadFromStorage() {
        loadFromChrome();
    }



    function saveToChrome() {
        localStorage.setItem('clipboardSave', JSON.stringify(saveObject));

    }



    function loadFromChrome() {
        if (localStorage.getItem('clipboardSave') !== null) {
            saveObject = JSON.parse(localStorage.getItem('clipboardSave'));

        }

    }


});

