var check = 0;
var notes = 0;
var cameras = 1;
var idButton, idWindow, val = "";
var cameraArray = [];
addCameraButton();
lockButton();
dragResize();
notesMode();
// Click to remove information
$(".information").on("click", function(){
  $(this).css("display", "none");
}
                    );
function sideScroll() {
  $("div").click(function() {
    alert($("div").scrollLeft());
  }
                );
}
function notesMode() {
  $('#notesMode').click(function() {
    if (notes === 0) {
      $('#notesMode').val("N-Mode On");
      $('#shortcuts .topbar').attr("class", "topbar red").text("Locked");
      notes = 1;
    }
    else {
      $('#shortcuts .topbar').attr("class", "topbar blue").text("Shortcuts");
      $('#notesMode').val("N-Mode Off");
      notes = 0;
    }
  }
                       );
}
function lockButton() {
  $('#lockAddCam').click(function() {
    if (check === 0) {
      $('#lockAddCam').attr("value", "Unlock");
      $('#lock').text('Locked').attr("class", "red");
      check = 1;
    }
    else {
      $('#lockAddCam').attr("value", "Lock");
      $('#lock').text('Unlocked').attr("class", "green");
      check = 0;
    }
  }
                        );
}
function addCameraButton() {
  $('#addCam').click(function() {
    if (check === 0) {
      idButton = 'cambutton' + cameras;
      idWindow = 'camwin' + cameras;
      $('#buttons').append("<button id='" + idButton + "' class='module redbut'>Camera" + cameras + "</button>");
      $('#container').append("<div class='frame red camwindows' id='" + idWindow + "'> <div class='topbar orange'>Camera " + cameras + "</div>");
      $('#shortcutsList').append("<li><span>camera key = </span><span>" + cameras + " </span><input type='button' class='change' value='Change'> <input type='text' class='shortcutsTextarea' id='character'" + cameras + "size='1' maxlength='1'  value=''></li>");
      cameras += 1;
      changeColor();
      dragResize();
      generateArray(idButton, idWindow, (cameras - 1));
      short();
      shortkeys();
    }
  }
                    );
}
function short() {
  $('.change').click(function() {
    var index = $(this).parent().index();
    val = $(this).next().val();
    if (val !== "") {
      $(this).prev().text(val);
      cameraArray[index].shortcut = val.toUpperCase().charCodeAt(0);
      $(this).next().val("");
    }
  }
                    );
}
function shorcutAction(sh) {
  for (var i = 0; i <= cameraArray.length; i++) {
    if (sh == cameraArray[i].shortcut) {
      $("#" + cameraArray[i].idButton).attr("class", "module").addClass("green");
      $("#" + cameraArray[i].idWindow).attr("class", "frame camwindows").addClass("green");
    }
    else {
      $("#" + cameraArray[i].idButton).attr("class", "module").addClass("red");
      $("#" + cameraArray[i].idWindow).attr("class", "frame camwindows").addClass("red");
    }
  }
}
function shortkeys() {
  $(window).bind('keydown', function(e) {
    if (notes === 0) {
      for (var i = 0; i <= cameraArray.length; i++) {
        if (e.keyCode == cameraArray[i].shortcut) {
          shorcutAction(cameraArray[i].shortcut);
        }
      }
    }
  }
                );
}
function generateArray(id, id2, cam) {
  var cameraObject = {
  };
  cameraObject.idButton = id;
  cameraObject.idWindow = id2;
  cameraObject.shortcut = cam.toString().charCodeAt(0);
  cameraArray.push(cameraObject);
}
function changeColor() {
  $('#buttons button').click(function() {
    var idStr = $(this).attr("id");
    for (var i = 0; i <= cameraArray.length-1; i++) {
      if (cameraArray[i].idButton == idStr) {
        $("#" + idStr).attr("class", "module").addClass("green");
        $("#" + cameraArray[i].idWindow).attr("class", "frame camwindows").addClass("green");
      }
      else {
        $("#" + cameraArray[i].idButton).attr("class", "module").addClass("red");
        $("#" + cameraArray[i].idWindow).attr("class", "frame camwindows").addClass("red");
      }
    }
  }
                            );
}
function dragResize() {
  $('.frame').mousedown(function() {
    $(".active").removeClass("active");
    $(this).addClass("active");
  }
                       );
  $('.frame').resizable({
    minWidth: 200,
    minHeight: 59
  }
                       ).draggable({
    handle: ".topbar"
  }
                                  );
}

