js.Multi_Level_Nav:
var dropDownToggle = document.getElementsByClassName("dropdown-toggle");

for (var i = 0; i < dropDownToggle.length; i++) {
    dropDownToggle[i].addEventListener('click', dropDownFunction, false);
}

function dropDownFunction(e){
  e.preventDefault();
  this.classList.toggle("toggle-on");
  this.parentElement.nextElementSibling.classList.toggle("toggled-on");
}

