  function mbCodePlayer(source, time, string, animation, size, top){
    var codePlayerElement = document.createElement("div");
    size = size || ["80%","20%"];
    top = top || "0";
    string = string || false;
    animation = animation || false;
    time = time || 50;
    codePlayerElement.className = "code-player-window";
    codePlayerElement.id = "codePlayer";
    codePlayerElement.style.textAlign = "center";
    codePlayerElement.style.position = "fixed";
    codePlayerElement.style.width = size[0];
    codePlayerElement.style.height = size[1];
    codePlayerElement.style.padding = ".5em";
    codePlayerElement.style.top = top;
    codePlayerElement.style.left = "0";
    codePlayerElement.style.right = "0";
    codePlayerElement.style.marginLeft = "auto";
    codePlayerElement.style.marginRight = "auto";
    codePlayerElement.style.textAlign = "center";
    codePlayerElement.style.textAlign = "center";
    codePlayerElement.style.textAlign = "center";
    codePlayerElement.style.backgroundColor = "#fff";
    codePlayerElement.style.border = "#aaa";
    codePlayerElement.style.textAlign = "center";
    codePlayerElement.style.boxShadow = "2px 2px 2px rgba(0,0,0,.5)";
    codePlayerElement.style.opacity = ".8";
    codePlayerElement.style.zIndex = "999";
    codePlayerElement.style.overflow = "auto";
    document.body.prepend(codePlayerElement);

    codePlayerElement.addEventListener("click", function(e){
      e.preventDefault();
        this.style.display = "none";
      });
    if(animation){
    if(string){
      mbCodePlayerMethod(source, "codePlayer", time, true );
    }else{
      mbCodePlayerMethod(source, "codePlayer", time, false );
    }
    }else{
      codePlayerElement.innerHTML = source;
    }

  }

  function mbCodePlayerMethod(source, targetElement, time, string) {
      var sourceString;
      if(string){
        sourceString = source;
      }else{
        sourceString = document.getElementById(source).innerHTML;
      }
      var target = document.getElementById(targetElement);
      var subString = "";
      var counter = 0;
      callBackMethod();

      function callBackMethod() {
          if (counter <= sourceString.length - 1) {
              subString += sourceString[counter];
              target.innerHTML = subString;
              counter++;
              setTimeout(function() {
                  callBackMethod();
              }, time);
          }
      }
  }

