js.mb Inspect Element:
function mbInspectElement(){
  var check = 0;
  var coord1, coord2, distanceTop, distanceLeft;
  mbInspectElementsCreate();
  document.onmouseover = function(event) {
    if(event.target == event.currentTarget){
      return;
    }
    constantCheck(event.target);
  };

  function constantCheck(target){
    target.addEventListener("click",function(event){
      event.stopImmediatePropagation();
      if(target.classList.contains("ct-outline")){
        target.classList.remove("ct-outline");
      }
      else{
        target.classList.add("ct-outline");
        if(check==1){
          distanceTop = target.offsetTop - coord1;
          distanceLeft = target.offsetLeft - coord2;
          document.querySelector('#ctInspectDim').innerHTML = "L: " + distanceLeft.toFixed(2) + " px<br>T: " + distanceTop.toFixed(2) + " px";
          check = 0;
        }
        else{
          coord1 = target.offsetTop + target.offsetHeight;
          coord2 = target.offsetLeft + target.offsetWidth;
          document.querySelector('#ctInspectDim').innerHTML = "Choose second target";
          check = 1;
        }
      }
    }
                           );
    document.querySelector('#ctInspectCoord').innerHTML = "Top: " + target.offsetTop.toFixed(2) + " px<br>Left: " + target.offsetLeft.toFixed(2) + " px";
    document.querySelector('#ctInspectWidth').innerHTML = "Height: " + target.offsetHeight + " px<br>Width: " + target.offsetWidth + " px";
  }

  function mbInspectElementsCreate(){
    var inspectWrapper = document.createElement("div");
    inspectWrapper.id = "ctInspectWrapper";
    var inspectWidth = document.createElement("div");
    inspectWidth.id = "ctInspectWidth";
    inspectWrapper.appendChild(inspectWidth);
    var inspectCoord = document.createElement("div");
    inspectCoord.id = "ctInspectCoord"; 
    inspectWrapper.appendChild(inspectCoord);
    var inspectDim = document.createElement("div");
    inspectDim.id = "ctInspectDim";  
    inspectDim.innerHTML = "Choose first target";
    inspectWrapper.appendChild(inspectDim);
    document.querySelector('body').prepend(inspectWrapper);

    //create style
    var css = '*:hover { outline: 1px solid #445878; } *:active{ outline: 1px solid #445878; }#ctInspectWrapper{ pointer-events:none; position:fixed; z-index:1; display:inline-block; bottom:15px; right:15px; width: 90px; background-color:#EEEFF7; border-radius:5px; border: 1px solid #31353D; opacity:.8; padding:5px; font-weight: 700; font-size:12px; line-height:16px;} #ctInspectWidth{ color:#1C1D21; border-bottom: 1px solid #1C1D21;} #ctInspectCoord{ color:#445878; border-bottom: 1px solid #445878;} #ctInspectDim{ color:#31353D;} .ct-outline{box-shadow: inset 0 0 0 2px #31353D;}';

    var head = document.head || document.getElementsByTagName('head')[0],
    style = document.createElement('style');
    style.type = 'text/css';
    if (style.styleSheet){
      style.styleSheet.cssText = css;
    } else {
      style.appendChild(document.createTextNode(css));
    }

    head.appendChild(style);
  }
}

css.ct_Vertical_Grid:
html{
    min-height:100%;/* make sure it is at least as tall as the viewport */
    position:relative;
}
body{
    margin: 0;
    padding: 0;
    height:100%; /* force the BODY element to match the height of the HTML element */
}
body::after{
    background-image: -webkit-linear-gradient(90deg,rgba(58, 87, 135, .3) 1px, transparent 1px);
    background-image: -moz-linear-gradient(90deg,rgba(58, 87, 135, .3) 1px, transparent 1px);
    background-image: -o-linear-gradient(90deg,rgba(58, 87, 135, .3) 1px, transparent 1px);
    background-image: linear-gradient(90deg,rgba(58, 87, 135, .3) 1px, transparent 1px);
    background-position: 0 0;
    background-size: 10px 100%;
    content:"";
    height: 100%;
    pointer-events: none;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: 999;
}

